<?phprequire 'global.php';$KB_LIST="";$query = $_SERVER['PHP_SELF'];$path = pathinfo( $query );$basename = $path['basename'];$max = 0;echo "<html>\n";echo "<body>\n";$DIR=opendir("$DOCUMENT_ROOT".$DIR_KB."/");//echo "<option>DIR_KB:".str_replace($basename,$DIR_KB,curPageURL())."/</option>\n";while ($file = readdir($DIR)){    if ($file!="." and $file!=".." and (endsWith($file,$EXT_KB) or endsWith($file,$EXT_KX))){        //echo "<option>";        //echo $file . filemtime($DIR_KB."/".$file) . date (" F d Y H:i:s.", filemtime($DIR_KB."/".$file));        //echo "</option>\n";        if (filemtime($DIR_KB."/".$file) > $max) $max = filemtime($DIR_KB."/".$file);    }}closedir($DIR);echo "<option>MAX_DATE:".date ("YmdHis", $max)."</option>\n";echo "</body>\n";echo "</html>";?>