<?phprequire 'global.php';$KB_LIST="";$query = $_SERVER['PHP_SELF'];$path = pathinfo( $query );$basename = $path['basename'];echo "<html>\n";echo "<body>\n";$DIR=opendir("$DOCUMENT_ROOT".$DIR_KB."/");echo "<option>DIR_KB:".str_replace($basename,$DIR_KB,curPageURL())."/</option>\n";while ($file = readdir($DIR)){    if ($file!="." and $file!=".." and (endsWith($file,$EXT_KB) or endsWith($file,$EXT_KX))){        echo "<option>$file</option>\n";    }}closedir($DIR);echo "</body>\n";echo "</html>";?>